/* -------------------------------------------------------------------- */
/** \name Simple Math Macros
 * \{ */

#define IS_EQ(a, b) ((fabs((double)((a) - (b))) >= (double)FLT_EPSILON) ? false : true)
#define IS_EQF(a, b) ((fabsf((float)((a) - (b))) >= (float)FLT_EPSILON) ? false : true)

/**
 * Although the naming of the arguments imply that `lo` and `hi` are ordered, we would hate to add an assert here so we handle
 * both cases (`lo` < `hi and `lo` > `hi`).
 */

#define IN_RANGE(v, lo, hi) (((lo) < (hi)) ? (((lo) < (v) && (v) < (hi))) : (((hi) < (v) && (v) < (lo))))
#define IN_RANGE_INCL(v, lo, hi) (((lo) < (hi)) ? (((lo) <= (v) && (v) <= (hi))) : (((hi) <= (v) && (v) <= (lo))))

/** \} */

/* -------------------------------------------------------------------- */
/** \name Array Unpacking Macros
 * \{ */

#define UNPACK2(a) ((a)[0]), ((a)[1])
#define UNPACK3(a) UNPACK2(a), ((a)[2])
#define UNPACK4(a) UNPACK3(a), ((a)[3])
/* pre may be '&', '*' or func, post may be '->member' */
#define UNPACK2_EX(pre, a, post) (pre((a)[0]) post), (pre((a)[1]) post)
#define UNPACK3_EX(pre, a, post) UNPACK2_EX(pre, a, post), (pre((a)[2]) post)
#define UNPACK4_EX(pre, a, post) UNPACK3_EX(pre, a, post), (pre((a)[3]) post)

/** \} */

/* -------------------------------------------------------------------- */
/** \name Array Macros
 * \{ */

#define ARRAY_HAS_ITEM(arr_item, arr_start, arr_len) ((size_t)((ptrdiff_t)(arr_item) - (ptrdiff_t)(arr_start)) < (size_t)(arr_len))

/** Return the number of elements in a static array of elements. */
#define ARRAY_SIZE(arr) (sizeof(arr) / sizeof(*(arr)))

/** \} */

/* -------------------------------------------------------------------- */
/** \name Pointer Macros
 * \{ */

#define POINTER_OFFSET(ptr, offset) (void *)(((char *)ptr) + (offset))

#define POINTER_FROM_INT(i) ((void *)(intptr_t)(i))
#define POINTER_AS_INT(i) ((void)0, ((int)(intptr_t)(i)))

#define POINTER_FROM_UINT(i) ((void *)(uintptr_t)(i))
#define POINTER_AS_UINT(i) ((void)0, ((unsigned int)(uintptr_t)(i)))

/** \} */

/* -------------------------------------------------------------------- */
/** \name Pointer Methods
 * \{ */

/**
 * Swap the contents of two memory blocks.
 *
 * This function swaps the contents of two memory blocks byte-by-byte.
 * Both memory blocks must be at least `n` bytes long.
 *
 * \param m1 A pointer to the first memory block.
 * \param m2 A pointer to the second memory block.
 * \param n The number of bytes to swap between the two memory blocks.
 *
 * \note This function is inefficient for swapping small memory blocks.
 */
void memswap(void *m1, void *m2, size_t n);

/** \} */

/* -------------------------------------------------------------------- */
/** \name String Macros
 * \{ */

/* Macro to convert a value to string in the preprocessor:
 * - `STRINGIFY_ARG`: gives the argument as a string
 * - `STRINGIFY_APPEND`: appends any argument 'b' onto the string argument 'a',
 *   used by `STRINGIFY` because some preprocessors warn about zero arguments.
 * - `STRINGIFY`: gives the argument's value as a string. */

#define STRINGIFY_ARG(x) "" #x
#define STRINGIFY_APPEND(a, b) "" a #b
#define STRINGIFY(x) STRINGIFY_APPEND("", x)

/* generic strcmp macros */
#if defined(_MSC_VER)
#	define strcasecmp _stricmp
#	define strncasecmp _strnicmp
#endif

#define STREQ(a, b) (strcmp(a, b) == 0)
#define STRCASEEQ(a, b) (strcasecmp(a, b) == 0)
#define STREQLEN(a, b, n) (strncmp(a, b, n) == 0)
#define STRCASEEQLEN(a, b, n) (strncasecmp(a, b, n) == 0)

#define STRPREFIX(a, b) (strncmp((a), (b), strlen(b)) == 0)

/** \} */

